// https://medium.freecodecamp.com/javascript-modules-a-beginner-s-guide-783f7d7a5fcc#.2jkpzjofs
// Defines a CommonJS module

function myModule() {
  this.hello = function() {
    return 'hello!';
  };

  this.goodbye = function() {
    return 'goodbye!';
  };
  
  this.sum = function(a, b) {
    return a + b;
  };
}

module.exports = myModule;
